import { useRouter } from "next/router";
import { connect } from "react-redux";
import values from "lodash/values";

import retrievePostActions from "store/retrievePost/actions";
import postActions from "store/posts/postActions";
import { IRetrievePost } from "interfaces";

const Posts: React.FC<{ post: IRetrievePost; fetchRetrievePosts: any }> = ({
  post,
  fetchRetrievePosts,
}) => {
  const router = useRouter();
  if (!post || post.id !== Number(router.query.postId)) {
    fetchRetrievePosts(router.query.postId);
  }
  console.log("router.query :", router.query);
  // const { postId } = router.query;

  return (
    post && (
      <div className="post">
        <div className="post__title">{post.title}</div>
        <div className="post__body">{post.body}</div>
        <div className="post__edit">
          <button>Update</button>
          <button>Delete</button>
        </div>
        <div className="post__add-comment"></div>
        <div className="post__comments">
          {values(post.comments).map((comment) => (
            <p>{comment.body}</p>
          ))}
        </div>
      </div>
    )
  );
};

const mapStateToProps = ({ retrievePostReducer }) => ({
  post: retrievePostReducer.post,
});
const mapDispatchToProps = { retrievePostActions, postActions };

export default connect(mapStateToProps, mapDispatchToProps)(Posts);
