import React, { useState } from "react";

import { useRouter } from "next/router";
import { connect } from "react-redux";
import values from "lodash/values";

import retrievePostActions from "store/retrievePost/actions";
import postActions from "store/posts/actions";
import { IRetrievePost } from "interfaces";
import store from "store";

const Posts: React.FC<{
  post: IRetrievePost;
  fetchRetrievePosts: any;
  fetchDeletePost: any;
}> = ({ post, fetchRetrievePosts, fetchDeletePost }) => {
  const router = useRouter();
  const [update, setUpdate] = useState(false);
  const [updateTitle, setUpdateTitle] = useState(post ? post.title : "");
  const [updateBody, setUpdateBody] = useState(post ? post.body : "");
  const postId: string | string[] = router.query.postId;

  if (!post || post.id !== Number(postId)) {
    fetchRetrievePosts(router.query.postId).then(() => {
      setUpdateTitle(store.getState().retrievePostReducer.post.title);
      setUpdateBody(store.getState().retrievePostReducer.post.body);
    });
  }
  // const { postId } = router.query;

  const changeTitle = (event) => {
    setUpdateTitle(event.target.value);
  };

  const changeBody = (event) => {
    setUpdateBody(event.target.value);
  };

  const deletePost = () => {
    fetchDeletePost(postId).then(() => {
      alert("Post deleted!");
      router.push("/");
    });
  };

  return (
    post && (
      <div className="post">
        <div className="post__title">{post.title}</div>
        <div className="post__body">{post.body}</div>
        <div className="post__edit">
          {!update ? (
            <>
              <button onClick={() => setUpdate(true)}>Update</button>
              <button onClick={deletePost}>Delete</button>
            </>
          ) : (
            <>
              <input type="text" value={updateTitle} onChange={changeTitle} />
              <input type="text" value={updateBody} onChange={changeBody} />
              <button
              // onClick={updatePost}
              >
                Confirm
              </button>
              <button onClick={() => setUpdate(false)}>Cancel</button>
            </>
          )}
        </div>
        <div className="post__add-comment"></div>
        <div className="post__comments">
          {values(post.comments).map((comment) => (
            <p>{comment.body}</p>
          ))}
        </div>
      </div>
    )
  );
};

const mapStateToProps = ({ retrievePostReducer }) => ({
  post: retrievePostReducer.post,
});
const mapDispatchToProps = {
  fetchRetrievePosts: retrievePostActions.fetchRetrievePosts,
  fetchDeletePost: postActions.fetchDeletePost,
};

export default connect(mapStateToProps, mapDispatchToProps)(Posts);
